01169         public static T[] GetActions<T>() where T : SteamVR_Action
01170         {
01171             Type type = typeof(T);
01172 
01173             if (type == typeof(SteamVR_Action))
01174             {
01175                 return actions as T[];
01176             }
01177             else if (type == typeof(ISteamVR_Action_In))
01178             {
01179                 return actionsIn as T[];
01180             }
01181             else if (type == typeof(ISteamVR_Action_Out))
01182             {
01183                 return actionsOut as T[];
01184             }
01185             else if (type == typeof(SteamVR_Action_Boolean))
01186             {
01187                 return actionsBoolean as T[];
01188             }
01189             else if (type == typeof(SteamVR_Action_Single))
01190             {
01191                 return actionsSingle as T[];
01192             }
01193             else if (type == typeof(SteamVR_Action_Vector2))
01194             {
01195                 return actionsVector2 as T[];
01196             }
01197             else if (type == typeof(SteamVR_Action_Vector3))
01198             {
01199                 return actionsVector3 as T[];
01200             }
01201             else if (type == typeof(SteamVR_Action_Pose))
01202             {
01203                 return actionsPose as T[];
01204             }
01205             else if (type == typeof(SteamVR_Action_Skeleton))
01206             {
01207                 return actionsSkeleton as T[];
01208             }
01209             else if (type == typeof(SteamVR_Action_Vibration))
01210             {
01211                 return actionsVibration as T[];
01212             }
01213             else
01214             {
01215                 Debug.Log("<b>[SteamVR]</b> Wrong type.");
01216             }
01217 
01218             return null;
01219         }
