01233         public static string GetLocalizedName(ulong originHandle, params EVRInputStringBits[] localizedParts)
01234         {
01235             int localizedPartsMask = 0;
01236 
01237             for (int partIndex = 0; partIndex < localizedParts.Length; partIndex++)
01238                 localizedPartsMask |= (int)localizedParts[partIndex];
01239 
01240             StringBuilder stringBuilder = new StringBuilder(500);
01241             OpenVR.Input.GetOriginLocalizedName(originHandle, stringBuilder, 500, localizedPartsMask);
01242 
01243             return stringBuilder.ToString();
01244         }
