01248         public static void IdentifyActionsFile(bool showLogs = true)
01249         {
01250             string currentPath = Application.dataPath;
01251             int lastIndex = currentPath.LastIndexOf('/');
01252             currentPath = currentPath.Remove(lastIndex, currentPath.Length - lastIndex);
01253 
01254             string fullPath = System.IO.Path.Combine(currentPath, SteamVR_Settings.instance.actionsFilePath);
01255             fullPath = fullPath.Replace("\\", "/");
01256 
01257             if (File.Exists(fullPath))
01258             {
01259                 EVRInputError err = OpenVR.Input.SetActionManifestPath(fullPath);
01260                 if (err != EVRInputError.None)
01261                     Debug.LogError("<b>[SteamVR]</b> Error loading action manifest into SteamVR: " + err.ToString());
01262                 else
01263                 {
01264                     int numActions = 0;
01265                     if (SteamVR_Input.actions != null)
01266                     {
01267                         numActions = SteamVR_Input.actions.Length;
01268 
01269                         if (showLogs)
01270                             Debug.Log(string.Format("<b>[SteamVR]</b> Successfully loaded {0} actions from action manifest into SteamVR ({1})", numActions, fullPath));
01271                     }
01272                     else
01273                     {
01274                         if (showLogs)
01275                             Debug.LogWarning("<b>[SteamVR]</b> No actions found, but the action manifest was loaded. This usually means you haven't generated actions. Window -> SteamVR Input -> Save and Generate.");
01276                     }
01277                 }
01278             }
01279             else
01280             {
01281                 if (showLogs)
01282                     Debug.LogError("<b>[SteamVR]</b> Could not find actions file at: " + fullPath);
01283             }
01284         }
