01289         public static bool HasFileInMemoryBeenModified()
01290         {
01291             string projectPath = Application.dataPath;
01292             int lastIndex = projectPath.LastIndexOf("/");
01293             projectPath = projectPath.Remove(lastIndex, projectPath.Length - lastIndex);
01294             actionsFilePath = Path.Combine(projectPath, SteamVR_Settings.instance.actionsFilePath);
01295 
01296             string jsonText = null;
01297 
01298             if (File.Exists(actionsFilePath))
01299             {
01300                 jsonText = System.IO.File.ReadAllText(actionsFilePath);
01301             }
01302             else
01303             {
01304                 return true;
01305             }
01306 
01307             string newHashFromFile = SteamVR_Utils.GetBadMD5Hash(jsonText);
01308 
01309             string newJSON = JsonConvert.SerializeObject(SteamVR_Input.actionFile, Formatting.Indented, new JsonSerializerSettings { NullValueHandling = NullValueHandling.Ignore });
01310 
01311             string newHashFromMemory = SteamVR_Utils.GetBadMD5Hash(newJSON);
01312 
01313             return newHashFromFile != newHashFromMemory;
01314         }
