01316         public static bool CreateEmptyActionsFile(bool completelyEmpty = false)
01317         {
01318             string projectPath = Application.dataPath;
01319             int lastIndex = projectPath.LastIndexOf("/");
01320             projectPath = projectPath.Remove(lastIndex, projectPath.Length - lastIndex);
01321             actionsFilePath = Path.Combine(projectPath, SteamVR_Settings.instance.actionsFilePath);
01322 
01323             if (File.Exists(actionsFilePath))
01324             {
01325                 Debug.LogErrorFormat("<b>[SteamVR]</b> Actions file already exists in project root: {0}", actionsFilePath);
01326                 return false;
01327             }
01328 
01329             actionFile = new SteamVR_Input_ActionFile();
01330 
01331             if (completelyEmpty == false)
01332             {
01333                 actionFile.action_sets.Add(SteamVR_Input_ActionFile_ActionSet.CreateNew());
01334                 actionFile.actions.Add(SteamVR_Input_ActionFile_Action.CreateNew(actionFile.action_sets[0].shortName,
01335                     SteamVR_ActionDirections.In, SteamVR_Input_ActionFile_ActionTypes.boolean));
01336             }
01337 
01338             string newJSON = JsonConvert.SerializeObject(actionFile, Formatting.Indented, new JsonSerializerSettings { NullValueHandling = NullValueHandling.Ignore });
01339 
01340             File.WriteAllText(actionsFilePath, newJSON);
01341 
01342             actionFile.InitializeHelperLists();
01343             fileInitialized = true;
01344             return true;
01345         }
