01361         public static bool InitializeFile(bool force = false, bool showErrors = true)
01362         {
01363             bool actionsFileExists = DoesActionsFileExist();
01364 
01365             string jsonText = null;
01366 
01367             if (actionsFileExists)
01368             {
01369                 jsonText = System.IO.File.ReadAllText(actionsFilePath);
01370             }
01371             else
01372             {
01373                 if (showErrors)
01374                     Debug.LogErrorFormat("<b>[SteamVR]</b> Actions file does not exist in project root: {0}", actionsFilePath);
01375 
01376                 return false;
01377             }
01378 
01379             if (fileInitialized == true || (fileInitialized == true && force == false))
01380             {
01381                 string newHash = SteamVR_Utils.GetBadMD5Hash(jsonText);
01382 
01383                 if (newHash == actionFileHash)
01384                 {
01385                     return true;
01386                 }
01387 
01388                 actionFileHash = newHash;
01389             }
01390 
01391             actionFile = Valve.Newtonsoft.Json.JsonConvert.DeserializeObject<SteamVR_Input_ActionFile>(jsonText);
01392             actionFile.InitializeHelperLists();
01393             fileInitialized = true;
01394             return true;
01395         }
