01401         public static bool DeleteManifestAndBindings()
01402         {
01403             if (DoesActionsFileExist() == false)
01404                 return false;
01405 
01406             InitializeFile();
01407 
01408             string[] filesToDelete = actionFile.GetFilesToCopy();
01409             foreach (string bindingFilePath in filesToDelete)
01410             {
01411                 FileInfo bindingFileInfo = new FileInfo(bindingFilePath);
01412                 bindingFileInfo.IsReadOnly = false;
01413                 File.Delete(bindingFilePath);
01414             }
01415 
01416             if (File.Exists(actionsFilePath))
01417             {
01418                 FileInfo actionFileInfo = new FileInfo(actionsFilePath);
01419                 actionFileInfo.IsReadOnly = false;
01420                 File.Delete(actionsFilePath);
01421 
01422                 actionFile = null;
01423                 fileInitialized = false;
01424 
01425                 return true;
01426             }
01427 
01428             return false;
01429         }
