01463         private static bool checkingSetup = false;
01464         private static bool openingSetup = false;
01465         public static bool IsOpeningSetup() { return openingSetup; }
01466         private static void CheckSetup()
01467         {
01468             if (checkingSetup == false && openingSetup == false && (SteamVR_Input.actions == null || SteamVR_Input.actions.Length == 0))
01469             {
01470                 checkingSetup = true;
01471                 Debug.Break();
01472 
01473                 bool open = UnityEditor.EditorUtility.DisplayDialog("[SteamVR]", "It looks like you haven't generated actions for SteamVR Input yet. Would you like to open the SteamVR Input window?", "Yes", "No");
01474                 if (open)
01475                 {
01476                     openingSetup = true;
01477                     UnityEditor.EditorApplication.isPlaying = false;
01478                     Type editorWindowType = FindType("Valve.VR.SteamVR_Input_EditorWindow");
01479                     if (editorWindowType != null)
01480                     {
01481                         var window = UnityEditor.EditorWindow.GetWindow(editorWindowType, false, "SteamVR Input", true);
01482                         if (window != null)
01483                             window.Show();
01484                     }
01485                 }
01486                 else
01487                 {
01488                     Debug.LogError("<b>[SteamVR]</b> This version of SteamVR will not work if you do not create and generate actions. Please open the SteamVR Input window or downgrade to version 1.2.3 (on github)");
01489                 }
01490                 checkingSetup = false;
01491             }
01492         }
