01494         private static Type FindType(string typeName)
01495         {
01496             var type = Type.GetType(typeName);
01497             if (type != null) return type;
01498             foreach (var a in AppDomain.CurrentDomain.GetAssemblies())
01499             {
01500                 type = a.GetType(typeName);
01501                 if (type != null)
01502                     return type;
01503             }
01504             return null;
01505         }
