00024         public void InitializeHelperLists()
00025         {
00026             foreach (var actionset in action_sets)
00027             {
00028                 actionset.actionsInList = new List<SteamVR_Input_ActionFile_Action>(actions.Where(action => action.path.StartsWith(actionset.name) && SteamVR_Input_ActionFile_ActionTypes.listIn.Contains(action.type)));
00029                 actionset.actionsOutList = new List<SteamVR_Input_ActionFile_Action>(actions.Where(action => action.path.StartsWith(actionset.name) && SteamVR_Input_ActionFile_ActionTypes.listOut.Contains(action.type)));
00030                 actionset.actionsList = new List<SteamVR_Input_ActionFile_Action>(actions.Where(action => action.path.StartsWith(actionset.name)));
00031             }
00032 
00033             foreach (var item in localization)
00034             {
00035                 localizationHelperList.Add(new SteamVR_Input_ActionFile_LocalizationItem(item));
00036             }
00037         }
00038 
00039         public void SaveHelperLists()
00040         {
00041             //fix actions list
00042             foreach (var actionset in action_sets)
00043             {
00044                 actionset.actionsList.Clear();
00045                 actionset.actionsList.AddRange(actionset.actionsInList);
00046                 actionset.actionsList.AddRange(actionset.actionsOutList);
00047             }
00048 
00049             actions.Clear();
00050 
00051             foreach (var actionset in action_sets)
00052             {
00053                 actions.AddRange(actionset.actionsInList);
00054                 actions.AddRange(actionset.actionsOutList);
00055             }
00056 
00057             localization.Clear();
00058             foreach (var item in localizationHelperList)
00059             {
00060                 Dictionary<string, string> localizationItem = new Dictionary<string, string>();
00061                 localizationItem.Add(SteamVR_Input_ActionFile_LocalizationItem.languageTagKeyName, item.language);
00062 
00063                 foreach (var itemItem in item.items)
00064                 {
00065                     localizationItem.Add(itemItem.Key, itemItem.Value);
00066                 }
00067 
00068                 localization.Add(localizationItem);
00069             }
00070         }
