00072         public static string GetShortName(string name)
00073         {
00074             string fullName = name;
00075             int lastSlash = fullName.LastIndexOf('/');
00076             if (lastSlash != -1)
00077             {
00078                 if (lastSlash == fullName.Length - 1)
00079                 {
00080                     fullName = fullName.Remove(lastSlash);
00081                     lastSlash = fullName.LastIndexOf('/');
00082                     if (lastSlash == -1)
00083                     {
00084                         return GetCodeFriendlyName(fullName);
00085                     }
00086                 }
00087                 return GetCodeFriendlyName(fullName.Substring(lastSlash + 1));
00088             }
00089 
00090             return GetCodeFriendlyName(fullName);
00091         }
00092 
00093         public static string GetCodeFriendlyName(string name)
00094         {
00095             name = name.Replace('/', '_').Replace(' ', '_');
00096 
00097             if (char.IsLetter(name[0]) == false)
00098                 name = "_" + name;
00099 
00100             for (int charIndex = 0; charIndex < name.Length; charIndex++)
00101             {
00102                 if (char.IsLetterOrDigit(name[charIndex]) == false && name[charIndex] != '_')
00103                 {
00104                     name = name.Remove(charIndex, 1);
00105                     name = name.Insert(charIndex, "_");
00106                 }
00107             }
00108 
00109             return name;
00110         }
