00112         public string[] GetFilesToCopy(bool throwErrors = false)
00113         {
00114             List<string> files = new List<string>();
00115 
00116             FileInfo actionFileInfo = new FileInfo(SteamVR_Input.actionsFilePath);
00117             string path = actionFileInfo.Directory.FullName;
00118 
00119             files.Add(SteamVR_Input.actionsFilePath);
00120 
00121             foreach (var binding in default_bindings)
00122             {
00123                 string bindingPath = Path.Combine(path, binding.binding_url);
00124 
00125                 if (File.Exists(bindingPath))
00126                     files.Add(bindingPath);
00127                 else
00128                 {
00129                     if (throwErrors)
00130                     {
00131                         Debug.LogError("<b>[SteamVR]</b> Could not bind binding file specified by the actions.json manifest: " + bindingPath);
00132                     }
00133                 }
00134             }
00135 
00136             return files.ToArray();
00137         }
