00139         public void CopyFilesToPath(string toPath, bool overwrite)
00140         {
00141             string[] files = SteamVR_Input.actionFile.GetFilesToCopy();
00142 
00143             foreach (string file in files)
00144             {
00145                 FileInfo bindingInfo = new FileInfo(file);
00146                 string newFilePath = Path.Combine(toPath, bindingInfo.Name);
00147 
00148                 bool exists = false;
00149                 if (File.Exists(newFilePath))
00150                     exists = true;
00151 
00152                 if (exists)
00153                 {
00154                     if (overwrite)
00155                     {
00156                         FileInfo existingFile = new FileInfo(newFilePath);
00157                         existingFile.IsReadOnly = false;
00158                         existingFile.Delete();
00159 
00160                         File.Copy(file, newFilePath);
00161 
00162                         RemoveAppKey(newFilePath);
00163 
00164                         Debug.Log("<b>[SteamVR]</b> Copied (overwrote) SteamVR Input file at path: " + newFilePath);
00165                     }
00166                     else
00167                     {
00168                         Debug.Log("<b>[SteamVR]</b> Skipped writing existing file at path: " + newFilePath);
00169                     }
00170                 }
00171                 else
00172                 {
00173                     File.Copy(file, newFilePath);
00174 
00175                     RemoveAppKey(newFilePath);
00176 
00177                     Debug.Log("<b>[SteamVR]</b> Copied SteamVR Input file to folder: " + newFilePath);
00178                 }
00179 
00180             }
00181         }
