00189         private const string findString_appKeyStart = "\"app_key\"";
00190         private const string findString_appKeyEnd = "\",";
00191         private static void RemoveAppKey(string newFilePath)
00192         {
00193             if (File.Exists(newFilePath))
00194             {
00195                 string jsonText = System.IO.File.ReadAllText(newFilePath);
00196 
00197                 string findString = "\"app_key\"";
00198                 int stringStart = jsonText.IndexOf(findString);
00199 
00200                 if (stringStart == -1)
00201                     return; //no app key
00202 
00203                 int stringEnd = jsonText.IndexOf("\",", stringStart);
00204 
00205                 if (stringEnd == -1)
00206                     return; //no end?
00207 
00208                 stringEnd += findString_appKeyEnd.Length;
00209 
00210                 int stringLength = stringEnd - stringStart;
00211 
00212                 string newJsonText = jsonText.Remove(stringStart, stringLength);
00213 
00214                 FileInfo file = new FileInfo(newFilePath);
00215                 file.IsReadOnly = false;
00216 
00217                 File.WriteAllText(newFilePath, newJsonText);
00218             }
00219         }
00220 
00221         public void Save(string path)
00222         {
00223             FileInfo existingActionsFile = new FileInfo(path);
00224             if (existingActionsFile.Exists)
00225             {
00226                 existingActionsFile.IsReadOnly = false;
00227             }
00228 
00229             //SanitizeActionFile(); //todo: shouldn't we be doing this?
00230 
00231             string json = JsonConvert.SerializeObject(this, Formatting.Indented, new JsonSerializerSettings { NullValueHandling = NullValueHandling.Ignore });
00232 
00233             File.WriteAllText(path, json);
00234         }
