00525     public class SteamVR_Input_ActionFile_LocalizationItem
00526     {
00527         public const string languageTagKeyName = "language_tag";
00528 
00529         public string language;
00530         public Dictionary<string, string> items = new Dictionary<string, string>();
00531 
00532         public SteamVR_Input_ActionFile_LocalizationItem(string newLanguage)
00533         {
00534             language = newLanguage;
00535         }
00536 
00537         public SteamVR_Input_ActionFile_LocalizationItem(Dictionary<string, string> dictionary)
00538         {
00539             if (dictionary == null)
00540                 return;
00541 
00542             if (dictionary.ContainsKey(languageTagKeyName))
00543                 language = (string)dictionary[languageTagKeyName];
00544             else
00545                 Debug.Log("<b>[SteamVR]</b> Input: Error in actions file, no language_tag in localization array item.");
00546 
00547             foreach (KeyValuePair<string, string> item in dictionary)
00548             {
00549                 if (item.Key != languageTagKeyName)
00550                     items.Add(item.Key, (string)item.Value);
00551             }
00552         }
00553     }
