00624             return toRemove;
00625         }
00626 
00627         protected static int RemoveOldLocalizations(List<SteamVR_Input_ActionFile_Action> removedActionList)
00628         {
00629             int count = 0;
00630 
00631             foreach (var action in removedActionList)
00632             {
00633                 foreach (var locDictionary in SteamVR_Input.actionFile.localization)
00634                 {
00635                     bool removed = locDictionary.Remove(action.name);
00636                     if (removed)
00637                         count++;
00638                 }
00639             }
00640 
00641             return count;
00642         }
