00644         protected static void RemoveOldActionsAndSetsFromBindings(List<SteamVR_Input_ActionFile_ActionSet> setsToRemove, List<SteamVR_Input_ActionFile_Action> actionsToRemove)
00645         {
00646             foreach (var defaultBindingItem in SteamVR_Input.actionFile.default_bindings)
00647             {
00648                 string currentBindingPath = defaultBindingItem.binding_url;
00649 
00650                 SteamVR_Input_BindingFile currentBindingFile = GetBindingFileObject(currentBindingPath);
00651                 if (currentBindingFile == null)
00652                 {
00653                     Debug.LogError("<b>[SteamVR]</b> There was an error deserializing the binding at path: " + currentBindingPath);
00654                     continue;
00655                 }
00656 
00657                 bool changed = false;
00658 
00659                 List<string> bindingListsToRemove = new List<string>();
00660                 foreach (var actionList in currentBindingFile.bindings)
00661                 {
00662                     if (setsToRemove.Any(set => set.name == actionList.Key))
00663                     {
00664                         bindingListsToRemove.Add(actionList.Key);
00665                         changed = true;
00666                         continue;
00667                     }
00668 
00669                     for (int chordIndex = 0; chordIndex < actionList.Value.chords.Count; chordIndex++)
00670                     {
00671                         var existingChord = actionList.Value.chords[chordIndex];
00672                         if (actionsToRemove.Any(action => action.name == existingChord.output))
00673                         {
00674                             actionList.Value.chords.Remove(existingChord);
00675                             chordIndex--;
00676                             changed = true;
00677                         }
00678                     }
00679 
00680                     for (int hapticIndex = 0; hapticIndex < actionList.Value.haptics.Count; hapticIndex++)
00681                     {
00682                         var existingHaptic = actionList.Value.haptics[hapticIndex];
00683                         if (actionsToRemove.Any(action => action.name == existingHaptic.output))
00684                         {
00685                             actionList.Value.haptics.Remove(existingHaptic);
00686                             hapticIndex--;
00687                             changed = true;
00688                         }
00689                     }
00690 
00691                     for (int poseIndex = 0; poseIndex < actionList.Value.poses.Count; poseIndex++)
00692                     {
00693                         var existingPose = actionList.Value.poses[poseIndex];
00694                         if (actionsToRemove.Any(action => action.name == existingPose.output))
00695                         {
00696                             actionList.Value.poses.Remove(existingPose);
00697                             poseIndex--;
00698                             changed = true;
00699                         }
00700                     }
00701 
00702                     for (int skeletonIndex = 0; skeletonIndex < actionList.Value.skeleton.Count; skeletonIndex++)
00703                     {
00704                         var existingSkeleton = actionList.Value.skeleton[skeletonIndex];
00705                         if (actionsToRemove.Any(action => action.name == existingSkeleton.output))
00706                         {
00707                             actionList.Value.skeleton.Remove(existingSkeleton);
00708                             skeletonIndex--;
00709                             changed = true;
00710                         }
00711                     }
00712 
00713                     for (int sourceIndex = 0; sourceIndex < actionList.Value.sources.Count; sourceIndex++)
00714                     {
00715                         var existingSource = actionList.Value.sources[sourceIndex];
00716                         if (actionsToRemove.Any(action => action.name == existingSource.GetOutput()))
00717                         {
00718                             actionList.Value.sources.Remove(existingSource);
00719                             sourceIndex--;
00720                             changed = true;
00721                         }
00722                     }
00723                 }
00724 
00725                 for (int bindingListToRemoveIndex = 0; bindingListToRemoveIndex < bindingListsToRemove.Count; bindingListToRemoveIndex++)
00726                 {
00727                     currentBindingFile.bindings.Remove(bindingListsToRemove[bindingListToRemoveIndex]);
00728                 }
00729 
00730                 if (changed)
00731                 {
00732                     WriteBindingFileObject(currentBindingFile, currentBindingPath);
00733                 }
00734             }
00735         }
