00737         protected static void RemoveOldPartialBindings(List<SteamVR_PartialInputBindings> partialBindingList)
00738         {
00739             List<SteamVR_Input_ActionFile_Action> actionsToRemove = RemoveOldActions(partialBindingList);
00740             List<SteamVR_Input_ActionFile_ActionSet> setsToRemove = RemoveOldActionSets(partialBindingList);
00741 
00742             int sets = setsToRemove.Count;
00743             int actions = actionsToRemove.Count;
00744             int locs = RemoveOldLocalizations(actionsToRemove);
00745 
00746             string dialogText = string.Format("We've found a old {0} action sets, {1} actions, and {2} localization entries from old versions of this partial binding. Would you like to remove them from the actions file and default bindings?", sets, actions, locs);
00747 
00748             bool confirm = EditorUtility.DisplayDialog("SteamVR Input", dialogText, "Import", "Cancel");
00749             if (confirm)
00750             {
00751                 RemoveOldActionsAndSetsFromBindings(setsToRemove, actionsToRemove);
00752 
00753                 SteamVR_Input.actionFile.Save(SteamVR_Input.actionsFilePath);
00754 
00755                 SteamVR_Input.InitializeFile(true); // reload after the save
00756             }
00757             else
00758             {
00759                 SteamVR_Input.InitializeFile(true); // reload since we actually removed the actions / sets to display this message
00760             }
00761         }
