00804         public static Dictionary<string, List<SteamVR_PartialInputBindings>> ScanForPartials()
00805         {
00806             string[] partialManifestPaths = Directory.GetFiles("Assets/", partialManifestFilename, SearchOption.AllDirectories);
00807             Dictionary<string, List<SteamVR_PartialInputBindings>> partialBindings = new Dictionary<string, List<SteamVR_PartialInputBindings>>();
00808 
00809             for (int partialIndex = 0; partialIndex < partialManifestPaths.Length; partialIndex++)
00810             {
00811                 string path = partialManifestPaths[partialIndex];
00812                 string jsonText = File.ReadAllText(path);
00813 
00814                 SteamVR_PartialInputBindings partialBinding = JsonConvert.DeserializeObject<SteamVR_PartialInputBindings>(jsonText);
00815                 partialBinding.path = path;
00816 
00817                 if (partialBindings.ContainsKey(partialBinding.name))
00818                 {
00819                     for (int versionIndex = 0; versionIndex < partialBindings[partialBinding.name].Count; versionIndex++)
00820                     {
00821                         if (partialBinding.version < partialBindings[partialBinding.name][versionIndex].version)
00822                             partialBindings[partialBinding.name].Insert(versionIndex, partialBinding);
00823                     }
00824                 }
00825                 else
00826                 {
00827                     partialBindings.Add(partialBinding.name, new List<SteamVR_PartialInputBindings>() { partialBinding });
00828                 }
00829             }
00830 
00831             return partialBindings;
00832         }
00833     }
