00856         public void Save()
00857         {
00858             FileInfo existingActionsFile = new FileInfo(path);
00859             if (existingActionsFile.Exists)
00860             {
00861                 existingActionsFile.IsReadOnly = false;
00862             }
00863 
00864             //SanitizeActionFile(); //todo: shouldn't we be doing this?
00865 
00866             string json = JsonConvert.SerializeObject(this, Formatting.Indented, new JsonSerializerSettings { NullValueHandling = NullValueHandling.Ignore });
00867 
00868             File.WriteAllText(path, json);
00869         }
