00699                     if (newName != SteamVR_Input.actionFile.action_sets[actionSetIndex].shortName)
00700                     {
00701                         string oldName = SteamVR_Input.actionFile.action_sets[actionSetIndex].name;
00702                         foreach (var action in SteamVR_Input.actionFile.actions)
00703                         {
00704                             if (action.actionSet == oldName)
00705                                 action.SetNewActionSet(newName);
00706                         }
00707 
00708                         SteamVR_Input.actionFile.action_sets[actionSetIndex].SetNewShortName(newName);
00709                     }
00710 
00711                     EditorGUILayout.BeginHorizontal();
00712 
00713                     int selectedUsage = -1;
00714                     for (int valueIndex = 0; valueIndex < SteamVR_Input_ActionFile_ActionSet_Usages.listValues.Length; valueIndex++)
00715                     {
00716                         if (SteamVR_Input_ActionFile_ActionSet_Usages.listValues[valueIndex] == SteamVR_Input.actionFile.action_sets[actionSetIndex].usage)
00717                         {
00718                             selectedUsage = valueIndex;
00719                             break;
00720                         }
00721                     }
00722 
00723                     int wasUsage = selectedUsage;
00724                     if (selectedUsage == -1)
00725                         selectedUsage = 1;
00726 
00727                     selectedUsage = EditorGUILayout.Popup(selectedUsage, SteamVR_Input_ActionFile_ActionSet_Usages.listDescriptions);
00728 
00729                     if (wasUsage != selectedUsage)
00730                     {
00731                         SteamVR_Input.actionFile.action_sets[actionSetIndex].usage = SteamVR_Input_ActionFile_ActionSet_Usages.listValues[selectedUsage];
00732                     }
00733 
00734                     EditorGUILayout.Space();
00735 
00736 
00737                     bool removeSet = GUILayout.Button("-");
00738                     if (removeSet)
00739                     {
00740                         bool confirm = EditorUtility.DisplayDialog("Confirmation", "Are you sure you want to delete this action set and all of its actions?.", "Delete", "Cancel");
00741                         if (confirm)
00742                         {
00743                             //todo: this doesn't work
00744                             SteamVR_Input.actionFile.action_sets.RemoveAt(selectedActionSet);
00745                             selectedActionSet = -1;
00746                             selectedAction = null;
00747 
00748                             InitializeLists();
00749                             break;
00750                         }
00751                     }
00752 
00753                     EditorGUILayout.Space();
00754 
00755                     EditorGUILayout.EndHorizontal();
00756                     EditorGUILayout.EndVertical();
00757                 }
00758                 else
00759                 {
00760                     bool pressedSet = GUILayout.Button(SteamVR_Input.actionFile.action_sets[actionSetIndex].shortName);
00761 
00762                     if (pressedSet)
00763                     {
00764                         selectedActionSet = actionSetIndex;
00765 
00766                         selectedActionIndex = -1;
00767                         selectedAction = null;
00768 
00769                         InitializeLists();
00770                     }
00771                 }
00772 
00773                 if (actionSetIndex < SteamVR_Input.actionFile.action_sets.Count - 1)
00774                     GUILayout.FlexibleSpace();
00775             }
00776 
00777             EditorGUILayout.Space();
00778 
00779             bool addSet = GUILayout.Button("+");
00780 
00781             if (addSet)
00782             {
00783                 SteamVR_Input_ActionFile_ActionSet newActionSet = new SteamVR_Input_ActionFile_ActionSet();
00784                 newActionSet.name = SteamVR_Input_ActionFile_ActionSet.CreateNewName();
00785 
00786                 SteamVR_Input.actionFile.action_sets.Add(newActionSet);
00787 
00788                 selectedActionSet = SteamVR_Input.actionFile.action_sets.Count - 1;
00789 
00790                 selectedActionIndex = -1;
00791                 selectedAction = null;
00792 
00793                 InitializeLists();
00794             }
00795 
00796             GUILayout.FlexibleSpace();
00797 
00798             EditorGUILayout.EndHorizontal();
00799         }
