00819         private void SanitizeActionFile()
00820         {
00821             foreach (var action in SteamVR_Input.actionFile.actions)
00822             {
00823                 if (action.type != SteamVR_Input_ActionFile_ActionTypes.skeleton)
00824                 {
00825                     if (string.IsNullOrEmpty(action.skeleton) == false)
00826                     {
00827                         action.skeleton = null; //todo: shouldn't have skeleton data for non skeleton types I think
00828                     }
00829                 }
00830             }
00831         }
