00024         private static void UpdateAppKey(string newFilePath, string executableName)
00025         {
00026             if (File.Exists(newFilePath))
00027             {
00028                 string jsonText = System.IO.File.ReadAllText(newFilePath);
00029 
00030                 string findString = "\"app_key\" : \"";
00031                 int stringStart = jsonText.IndexOf(findString);
00032 
00033                 if (stringStart == -1)
00034                 {
00035                     findString = findString.Replace(" ", "");
00036                     stringStart = jsonText.IndexOf(findString);
00037 
00038                     if (stringStart == -1)
00039                         return; //no app key
00040                 }
00041 
00042                 stringStart += findString.Length;
00043                 int stringEnd = jsonText.IndexOf("\"", stringStart);
00044 
00045                 int stringLength = stringEnd - stringStart;
00046 
00047                 string currentAppKey = jsonText.Substring(stringStart, stringLength);
00048 
00049                 if (string.Equals(currentAppKey, SteamVR_Settings.instance.editorAppKey, System.StringComparison.CurrentCultureIgnoreCase) == false)
00050                 {
00051                     jsonText = jsonText.Replace(currentAppKey, SteamVR_Settings.instance.editorAppKey);
00052 
00053                     FileInfo file = new FileInfo(newFilePath);
00054                     file.IsReadOnly = false;
00055 
00056                     File.WriteAllText(newFilePath, jsonText);
00057                 }
00058             }
00059         }
