00003 using UnityEngine;
00004 using System.Collections;
00005 using System;
00006 using Valve.VR;
00007 using System.Linq;
00008 using System.Runtime.InteropServices;
00009 using System.ComponentModel;
00010 using System.Collections.Generic;
00011 
00012 namespace Valve.VR
00013 {
00014     public static class SteamVR_Input_Source
00015     {
00016         private static Dictionary<SteamVR_Input_Sources, ulong> inputSourceHandlesBySource = new Dictionary<SteamVR_Input_Sources, ulong>(new SteamVR_Input_Sources_Comparer());
00017         private static Dictionary<ulong, SteamVR_Input_Sources> inputSourceSourcesByHandle = new Dictionary<ulong, SteamVR_Input_Sources>();
00018 
00019         private static Type enumType = typeof(SteamVR_Input_Sources);
00020         private static Type descriptionType = typeof(DescriptionAttribute);
00021         
00022         private static SteamVR_Input_Sources[] allSources;
00023 
00024         public static ulong GetHandle(SteamVR_Input_Sources inputSource)
00025         {
00026             if (inputSourceHandlesBySource.ContainsKey(inputSource))
00027                 return inputSourceHandlesBySource[inputSource];
00028 
00029             return 0;
00030         }
00031         public static SteamVR_Input_Sources GetSource(ulong handle)
00032         {
00033             if (inputSourceSourcesByHandle.ContainsKey(handle))
00034                 return inputSourceSourcesByHandle[handle];
00035 
00036             return SteamVR_Input_Sources.Any;
00037         }
00038 
00039         public static SteamVR_Input_Sources[] GetAllSources()
00040         {
00041             if (allSources == null)
00042                 allSources = (SteamVR_Input_Sources[])System.Enum.GetValues(typeof(SteamVR_Input_Sources));
00043 
00044             return allSources;
00045         }
00046 
00047         private static string GetPath(string inputSourceEnumName)
00048         {
00049             return ((DescriptionAttribute)enumType.GetMember(inputSourceEnumName)[0].GetCustomAttributes(descriptionType, false)[0]).Description;
00050         }
00051 
00052         public static void Initialize()
00053         {
00054             List<SteamVR_Input_Sources> allSourcesList = new List<SteamVR_Input_Sources>();
00055             string[] enumNames = System.Enum.GetNames(enumType);
00056             inputSourceHandlesBySource = new Dictionary<SteamVR_Input_Sources, ulong>(new SteamVR_Input_Sources_Comparer());
00057             inputSourceSourcesByHandle = new Dictionary<ulong, SteamVR_Input_Sources>();
00058 
00059             for (int enumIndex = 0; enumIndex < enumNames.Length; enumIndex++)
00060             {
00061                 string path = GetPath(enumNames[enumIndex]);
00062 
00063                 ulong handle = 0;
00064                 EVRInputError err = OpenVR.Input.GetInputSourceHandle(path, ref handle);
00065 
00066                 if (err != EVRInputError.None)
00067                     Debug.LogError("<b>[SteamVR]</b> GetInputSourceHandle (" + path + ") error: " + err.ToString());
00068 
00069                 if (enumNames[enumIndex] == SteamVR_Input_Sources.Any.ToString()) //todo: temporary hack
00070                 {
00071                     inputSourceHandlesBySource.Add((SteamVR_Input_Sources)enumIndex, 0);
00072                     inputSourceSourcesByHandle.Add(0, (SteamVR_Input_Sources)enumIndex);
00073                 }
00074                 else
00075                 {
00076                     inputSourceHandlesBySource.Add((SteamVR_Input_Sources)enumIndex, handle);
00077                     inputSourceSourcesByHandle.Add(handle, (SteamVR_Input_Sources)enumIndex);
00078                 }
00079 
00080                 allSourcesList.Add((SteamVR_Input_Sources)enumIndex);
00081             }
00082             
00083             allSources = allSourcesList.ToArray();
00084         }
00085     }
00086 }
