00070 
00071             boneCount = skeletonMainPose.leftHand.bonePositions.Length;
00072             // NOTE: Is there a better way to get the bone count? idk
00073             blendedSnapshotL = new SteamVR_Skeleton_PoseSnapshot(boneCount, SteamVR_Input_Sources.LeftHand);
00074             blendedSnapshotR = new SteamVR_Skeleton_PoseSnapshot(boneCount, SteamVR_Input_Sources.RightHand);
