00083         public void SetBlendingBehaviourValue(string behaviourName, float value)
00084         {
00085             PoseBlendingBehaviour behaviour = blendingBehaviours.Find(b => b.name == behaviourName);
00086             if(behaviour == null)
00087             {
00088                 Debug.LogError("[SteamVR] Blending Behaviour: " + behaviourName + " not found on Skeleton Poser: " + gameObject.name);
00089                 return;
00090             }
00091             if(behaviour.type != PoseBlendingBehaviour.BlenderTypes.Manual)
00092             {
00093                 Debug.LogWarning("[SteamVR] Blending Behaviour: " + behaviourName + " is not a manual behaviour. Its value will likely be overriden.");
00094             }
