00194         public void UpdatePose(SteamVR_Action_Skeleton skeletonAction, SteamVR_Input_Sources inputSource)
00195         {
00196             // only allow this function to run once per frame
00197             if (poseUpdatedThisFrame) return;
00198 
00199             poseUpdatedThisFrame = true;
00200 
00201             // always do additive animation on main pose
00202             blendPoses[0].UpdateAdditiveAnimation(skeletonAction, inputSource);
00203 
00204             //copy from main pose as a base
00205             SteamVR_Skeleton_PoseSnapshot snap = GetHandSnapshot(inputSource);
00206             snap.CopyFrom(blendPoses[0].GetHandSnapshot(inputSource));
00207 
00208             ApplyBlenderBehaviours(skeletonAction, inputSource, snap);
00209 
00210 
00211             if (inputSource == SteamVR_Input_Sources.RightHand)
00212                 blendedSnapshotR = snap;
00213             if (inputSource == SteamVR_Input_Sources.LeftHand)
00214                 blendedSnapshotL = snap;
00215         }
00216 
00217         protected void ApplyBlenderBehaviours(SteamVR_Action_Skeleton skeletonAction, SteamVR_Input_Sources inputSource, SteamVR_Skeleton_PoseSnapshot snapshot)
00218         {
00219 
00220             // apply blending for each behaviour
00221             for (int behaviourIndex = 0; behaviourIndex < blendingBehaviours.Count; behaviourIndex++)
00222             {
00223                 blendingBehaviours[behaviourIndex].Update(Time.deltaTime, inputSource);
00224                 // if disabled or very low influence, skip for perf
00225                 if (blendingBehaviours[behaviourIndex].enabled && blendingBehaviours[behaviourIndex].influence * blendingBehaviours[behaviourIndex].value > 0.01f)
00226                 {
00227                     if (blendingBehaviours[behaviourIndex].pose != 0)
00228                     {
00229                         // update additive animation only as needed
00230                         blendPoses[blendingBehaviours[behaviourIndex].pose].UpdateAdditiveAnimation(skeletonAction, inputSource);
00231                     }
00232 
00233                     blendingBehaviours[behaviourIndex].ApplyBlending(snapshot, blendPoses, inputSource);
00234                 }
00235             }
00236 
00237         }
00238 
00239         protected void LateUpdate()
00240         {
00241             // let the pose be updated again the next frame
00242             poseUpdatedThisFrame = false;
00243         }
00244         
00246         protected Vector3 BlendVectors(Vector3[] vectors, float[] weights)
00247         {
00248             Vector3 blendedVector = Vector3.zero;
00249             for (int i = 0; i < vectors.Length; i++)
00250             {
00251                 blendedVector += vectors[i] * weights[i];
00252             }
00253             return blendedVector;
00254         }
00255 
00257         protected Quaternion BlendQuaternions(Quaternion[] quaternions, float[] weights)
00258         {
00259             Quaternion outquat = Quaternion.identity;
00260             for (int i = 0; i < quaternions.Length; i++)
00261             {
00262                 outquat *= Quaternion.Slerp(Quaternion.identity, quaternions[i], weights[i]);
00263             }
00264             return outquat;
00265         }
