00271         public class SkeletonBlendablePose
00272         {
00273             public SteamVR_Skeleton_Pose pose;
00274             public SteamVR_Skeleton_PoseSnapshot snapshotR;
00275             public SteamVR_Skeleton_PoseSnapshot snapshotL;
00276 
00280             public SteamVR_Skeleton_PoseSnapshot GetHandSnapshot(SteamVR_Input_Sources inputSource)
00281             {
00282                 if (inputSource == SteamVR_Input_Sources.LeftHand)
00283                 {
00284                     return snapshotL;
00285                 }
00286                 else
00287                 {
00288                     return snapshotR;
00289                 }
00290             }
00291 
00292             public void UpdateAdditiveAnimation(SteamVR_Action_Skeleton skeletonAction, SteamVR_Input_Sources inputSource)
00293             {
00294                 SteamVR_Skeleton_PoseSnapshot snapshot = GetHandSnapshot(inputSource);
00295                 SteamVR_Skeleton_Pose_Hand poseHand = pose.GetHand(inputSource);
00296 
00297                 for (int boneIndex = 0; boneIndex < snapshotL.bonePositions.Length; boneIndex++)
00298                 {
00299                     int fingerIndex = SteamVR_Skeleton_JointIndexes.GetFingerForBone(boneIndex);
00300                     SteamVR_Skeleton_FingerExtensionTypes extensionType = poseHand.GetMovementTypeForBone(boneIndex);
00301 
00302                     if (extensionType == SteamVR_Skeleton_FingerExtensionTypes.Free)
00303                     {
00304                         snapshot.bonePositions[boneIndex] = skeletonAction.bonePositions[boneIndex];
00305                         snapshot.boneRotations[boneIndex] = skeletonAction.boneRotations[boneIndex];
00306                     }
00307                     if (extensionType == SteamVR_Skeleton_FingerExtensionTypes.Extend)
00308                     {
00309                         // lerp to open pose by fingercurl
00310                         snapshot.bonePositions[boneIndex] = Vector3.Lerp(poseHand.bonePositions[boneIndex], skeletonAction.bonePositions[boneIndex], 1 - skeletonAction.fingerCurls[fingerIndex]);
00311                         snapshot.boneRotations[boneIndex] = Quaternion.Lerp(poseHand.boneRotations[boneIndex], skeletonAction.boneRotations[boneIndex], 1 - skeletonAction.fingerCurls[fingerIndex]);
00312                     }
00313                     if (extensionType == SteamVR_Skeleton_FingerExtensionTypes.Contract)
00314                     {
00315                         // lerp to closed pose by fingercurl
00316                         snapshot.bonePositions[boneIndex] = Vector3.Lerp(poseHand.bonePositions[boneIndex], skeletonAction.bonePositions[boneIndex], skeletonAction.fingerCurls[fingerIndex]);
00317                         snapshot.boneRotations[boneIndex] = Quaternion.Lerp(poseHand.boneRotations[boneIndex], skeletonAction.boneRotations[boneIndex], skeletonAction.fingerCurls[fingerIndex]);
00318                     }
00319                 }
00320             }
00321 
00325             public SkeletonBlendablePose(SteamVR_Skeleton_Pose p)
00326             {
00327                 pose = p;
00328                 snapshotR = new SteamVR_Skeleton_PoseSnapshot(p.rightHand.bonePositions.Length, SteamVR_Input_Sources.RightHand);
00329                 snapshotL = new SteamVR_Skeleton_PoseSnapshot(p.leftHand.bonePositions.Length, SteamVR_Input_Sources.LeftHand);
00330             }
00331 
00335             public void PoseToSnapshots()
00336             {
00337                 snapshotR.position = pose.rightHand.position;
00338                 snapshotR.rotation = pose.rightHand.rotation;
00339                 pose.rightHand.bonePositions.CopyTo(snapshotR.bonePositions, 0);
00340                 pose.rightHand.boneRotations.CopyTo(snapshotR.boneRotations, 0);
00341 
00342                 snapshotL.position = pose.leftHand.position;
00343                 snapshotL.rotation = pose.leftHand.rotation;
00344                 pose.leftHand.bonePositions.CopyTo(snapshotL.bonePositions, 0);
00345                 pose.leftHand.boneRotations.CopyTo(snapshotL.boneRotations, 0);
00346             }
00347 
00348             public SkeletonBlendablePose() { }
00349         }
