00354         [System.Serializable]
00355         public class PoseBlendingBehaviour
00356         {
00357             public string name;
00358             public bool enabled = true;
00359             public float influence = 1;
00360             public int pose = 1;
00361             public float value = 0;
00362             public SteamVR_Action_Single action_single;
00363             public SteamVR_Action_Boolean action_bool;
00364             public float smoothingSpeed = 0;
00365             public BlenderTypes type;
00366             public bool useMask;
00367             public SteamVR_Skeleton_HandMask mask = new SteamVR_Skeleton_HandMask();
00368 
00369             public bool previewEnabled;
00370 
00374             public void Update(float deltaTime, SteamVR_Input_Sources inputSource)
00375             {
00376                 if (type == BlenderTypes.AnalogAction)
00377                 {
00378                     if (smoothingSpeed == 0)
00379                         value = action_single.GetAxis(inputSource);
00380                     else
00381                         value = Mathf.Lerp(value, action_single.GetAxis(inputSource), deltaTime * smoothingSpeed);
00382                 }
00383                 if (type == BlenderTypes.BooleanAction)
00384                 {
00385                     if (smoothingSpeed == 0)
00386                         value = action_bool.GetState(inputSource) ? 1 : 0;
00387                     else
00388                         value = Mathf.Lerp(value, action_bool.GetState(inputSource) ? 1 : 0, deltaTime * smoothingSpeed);
00389                 }
00390             }
00391 
00398             public void ApplyBlending(SteamVR_Skeleton_PoseSnapshot snapshot, SkeletonBlendablePose[] blendPoses, SteamVR_Input_Sources inputSource)
00399             {
00400                 SteamVR_Skeleton_PoseSnapshot targetSnapshot = blendPoses[pose].GetHandSnapshot(inputSource);
00401                 if (mask.GetFinger(0) || useMask == false)
00402                 {
00403                     snapshot.position = Vector3.Lerp(snapshot.position, targetSnapshot.position, influence * value);
00404                     snapshot.rotation = Quaternion.Slerp(snapshot.rotation, targetSnapshot.rotation, influence * value);
00405                 }
00406 
00407                 for (int boneIndex = 0; boneIndex < snapshot.bonePositions.Length; boneIndex++)
00408                 {
00409                     // verify the current finger is enabled in the mask, or if no mask is used.
00410                     if (mask.GetFinger(SteamVR_Skeleton_JointIndexes.GetFingerForBone(boneIndex) + 1) || useMask == false)
00411                     {
00412                         snapshot.bonePositions[boneIndex] = Vector3.Lerp(snapshot.bonePositions[boneIndex], targetSnapshot.bonePositions[boneIndex], influence * value);
00413                         snapshot.boneRotations[boneIndex] = Quaternion.Slerp(snapshot.boneRotations[boneIndex], targetSnapshot.boneRotations[boneIndex], influence * value);
00414                     }
00415                 }
00416             }
00417 
00418             public PoseBlendingBehaviour()
00419             {
00420                 enabled = true;
00421                 influence = 1;
00422             }
00423 
00424             public enum BlenderTypes
00425             {
00426                 Manual, AnalogAction, BooleanAction
00427             }
00428         }
