00435     public class SteamVR_Skeleton_PoseSnapshot
00436     {
00437         public SteamVR_Input_Sources inputSource;
00438 
00439         public Vector3 position;
00440         public Quaternion rotation;
00441 
00442         public Vector3[] bonePositions;
00443         public Quaternion[] boneRotations;
00444 
00445         public SteamVR_Skeleton_PoseSnapshot(int boneCount, SteamVR_Input_Sources source)
00446         {
00447             inputSource = source;
00448             bonePositions = new Vector3[boneCount];
00449             boneRotations = new Quaternion[boneCount];
00450             position = Vector3.zero;
00451             rotation = Quaternion.identity;
00452         }
00453 
00457         public void CopyFrom(SteamVR_Skeleton_PoseSnapshot source)
00458         {
00459             inputSource = source.inputSource;
00460             position = source.position;
00461             rotation = source.rotation;
00462             for (int i = 0; i < bonePositions.Length; i++)
00463             {
00464                 bonePositions[i] = source.bonePositions[i];
00465                 boneRotations[i] = source.boneRotations[i];
00466             }
00467         }
00468     }
