00473     [System.Serializable]
00474     public class SteamVR_Skeleton_HandMask
00475     {
00476         public bool palm;
00477         public bool thumb;
00478         public bool index;
00479         public bool middle;
00480         public bool ring;
00481         public bool pinky;
00482         public bool[] values = new bool[6];
00483 
00484         public void SetFinger(int i, bool value)
00485         {
00486             values[i] = value;
00487             Apply();
00488         }
00489 
00490         public bool GetFinger(int i)
00491         {
00492             return values[i];
00493         }
00494 
00495         public SteamVR_Skeleton_HandMask()
00496         {
00497             values = new bool[6];
00498             Reset();
00499         }
00500 
00504         public void Reset()
00505         {
00506             values = new bool[6];
00507             for (int i = 0; i < 6; i++)
00508             {
00509                 values[i] = true;
00510             }
00511             Apply();
00512         }
00513 
00514         protected void Apply()
00515         {
00516             palm = values[0];
00517             thumb = values[1];
00518             index = values[2];
00519             middle = values[3];
00520             ring = values[4];
00521             pinky = values[5];
00522         }
00523 
00524         public static readonly SteamVR_Skeleton_HandMask fullMask = new SteamVR_Skeleton_HandMask();
00525     };
