00016     [CustomEditor(typeof(SteamVR_Skybox)), CanEditMultipleObjects]
00017     public class SteamVR_SkyboxEditor : Editor
00018     {
00019         private const string nameFormat = "{0}/{1}-{2}.png";
00020         private const string helpText = "Take snapshot will use the current " +
00021             "position and rotation to capture six directional screenshots to use as this " +
00022             "skybox's textures.  Note: This skybox is only used to override what shows up " +
00023             "in the compositor (e.g. when loading levels).  Add a Camera component to this " +
00024             "object to override default settings like which layers to render.  Additionally, " +
00025             "by specifying your own targetTexture, you can control the size of the textures " +
00026             "and other properties like antialiasing.  Don't forget to disable the camera.\n\n" +
00027             "For stereo screenshots, a panorama is render for each eye using the specified " +
00028             "ipd (in millimeters) broken up into segments cellSize pixels square to optimize " +
00029             "generation.\n(32x32 takes about 10 seconds depending on scene complexity, 16x16 " +
00030             "takes around a minute, while will 8x8 take several minutes.)\n\nTo test, hit " +
00031             "play then pause - this will activate the skybox settings, and then drop you to " +
00032             "the compositor where the skybox is rendered.";
00033 
00034         public override void OnInspectorGUI()
00035         {
00036             DrawDefaultInspector();
00037 
00038             EditorGUILayout.HelpBox(helpText, MessageType.Info);
00039 
00040             if (GUILayout.Button("Take snapshot"))
00041             {
00042                 var directions = new Quaternion[] {
00043                 Quaternion.LookRotation(Vector3.forward),
00044                 Quaternion.LookRotation(Vector3.back),
00045                 Quaternion.LookRotation(Vector3.left),
00046                 Quaternion.LookRotation(Vector3.right),
00047                 Quaternion.LookRotation(Vector3.up, Vector3.back),
00048                 Quaternion.LookRotation(Vector3.down, Vector3.forward)
00049             };
00050 
00051                 Camera tempCamera = null;
00052                 foreach (SteamVR_Skybox target in targets)
00053                 {
00054                     var targetScene = target.gameObject.scene;
00055                     var sceneName = Path.GetFileNameWithoutExtension(targetScene.path);
00056                     var scenePath = Path.GetDirectoryName(targetScene.path);
00057                     var assetPath = scenePath + "/" + sceneName;
00058                     if (!AssetDatabase.IsValidFolder(assetPath))
00059                     {
00060                         var guid = AssetDatabase.CreateFolder(scenePath, sceneName);
00061                         assetPath = AssetDatabase.GUIDToAssetPath(guid);
00062                     }
00063 
00064                     var camera = target.GetComponent<Camera>();
00065                     if (camera == null)
00066                     {
00067                         if (tempCamera == null)
00068                             tempCamera = new GameObject().AddComponent<Camera>();
00069                         camera = tempCamera;
00070                     }
00071 
00072                     var targetTexture = camera.targetTexture;
00073                     if (camera.targetTexture == null)
00074                     {
00075                         targetTexture = new RenderTexture(1024, 1024, 24);
00076                         targetTexture.antiAliasing = 8;
00077                         camera.targetTexture = targetTexture;
00078                     }
00079 
00080                     var oldPosition = target.transform.localPosition;
00081                     var oldRotation = target.transform.localRotation;
00082                     var baseRotation = target.transform.rotation;
00083 
00084                     var t = camera.transform;
00085                     t.position = target.transform.position;
00086                     camera.orthographic = false;
00087                     camera.fieldOfView = 90;
00088 
00089                     for (int i = 0; i < directions.Length; i++)
00090                     {
00091                         t.rotation = baseRotation * directions[i];
00092                         camera.Render();
00093 
00094                         // Copy to texture and save to disk.
00095                         RenderTexture.active = targetTexture;
00096                         var texture = new Texture2D(targetTexture.width, targetTexture.height, TextureFormat.ARGB32, false);
00097                         texture.ReadPixels(new Rect(0, 0, texture.width, texture.height), 0, 0);
00098                         texture.Apply();
00099                         RenderTexture.active = null;
00100 
00101                         var assetName = string.Format(nameFormat, assetPath, target.name, i);
00102                         System.IO.File.WriteAllBytes(assetName, texture.EncodeToPNG());
00103                     }
00104 
00105                     if (camera != tempCamera)
00106                     {
00107                         target.transform.localPosition = oldPosition;
00108                         target.transform.localRotation = oldRotation;
00109                     }
00110                 }
00111 
00112                 if (tempCamera != null)
00113                 {
00114                     Object.DestroyImmediate(tempCamera.gameObject);
00115                 }
00116 
00117                 // Now that everything has be written out, reload the associated assets and assign them.
00118                 AssetDatabase.Refresh();
00119                 foreach (SteamVR_Skybox target in targets)
00120                 {
00121                     var targetScene = target.gameObject.scene;
00122                     var sceneName = Path.GetFileNameWithoutExtension(targetScene.path);
00123                     var scenePath = Path.GetDirectoryName(targetScene.path);
00124                     var assetPath = scenePath + "/" + sceneName;
00125 
00126                     for (int i = 0; i < directions.Length; i++)
00127                     {
00128                         var assetName = string.Format(nameFormat, assetPath, target.name, i);
00129                         var importer = AssetImporter.GetAtPath(assetName) as TextureImporter;
00130 #if (UNITY_5_4 || UNITY_5_3 || UNITY_5_2 || UNITY_5_1 || UNITY_5_0)
00131                         importer.textureFormat = TextureImporterFormat.RGB24;
00132 #else
00133                     importer.textureCompression = TextureImporterCompression.Uncompressed;
00134 #endif
00135                         importer.wrapMode = TextureWrapMode.Clamp;
00136                         importer.mipmapEnabled = false;
00137                         importer.SaveAndReimport();
00138 
00139                         var texture = AssetDatabase.LoadAssetAtPath<Texture>(assetName);
00140                         target.SetTextureByIndex(i, texture);
00141                     }
00142                 }
00143             }
00144             else if (GUILayout.Button("Take stereo snapshot"))
00145             {
00146                 const int width = 4096;
00147                 const int height = width / 2;
00148                 const int halfHeight = height / 2;
00149 
00150                 var textures = new Texture2D[] {
00151                 new Texture2D(width, height, TextureFormat.ARGB32, false),
00152                 new Texture2D(width, height, TextureFormat.ARGB32, false) };
00153 
00154                 var timer = new System.Diagnostics.Stopwatch();
00155 
00156                 Camera tempCamera = null;
00157                 foreach (SteamVR_Skybox target in targets)
00158                 {
00159                     timer.Start();
00160 
00161                     var targetScene = target.gameObject.scene;
00162                     var sceneName = Path.GetFileNameWithoutExtension(targetScene.path);
00163                     var scenePath = Path.GetDirectoryName(targetScene.path);
00164                     var assetPath = scenePath + "/" + sceneName;
00165                     if (!AssetDatabase.IsValidFolder(assetPath))
00166                     {
00167                         var guid = AssetDatabase.CreateFolder(scenePath, sceneName);
00168                         assetPath = AssetDatabase.GUIDToAssetPath(guid);
00169                     }
00170 
00171                     var camera = target.GetComponent<Camera>();
00172                     if (camera == null)
00173                     {
00174                         if (tempCamera == null)
00175                             tempCamera = new GameObject().AddComponent<Camera>();
00176                         camera = tempCamera;
00177                     }
00178 
00179                     var fx = camera.gameObject.AddComponent<SteamVR_SphericalProjection>();
00180 
00181                     var oldTargetTexture = camera.targetTexture;
00182                     var oldOrthographic = camera.orthographic;
00183                     var oldFieldOfView = camera.fieldOfView;
00184                     var oldAspect = camera.aspect;
00185 
00186                     var oldPosition = target.transform.localPosition;
00187                     var oldRotation = target.transform.localRotation;
00188                     var basePosition = target.transform.position;
00189                     var baseRotation = target.transform.rotation;
00190 
00191                     var transform = camera.transform;
00192 
00193                     int cellSize = int.Parse(target.StereoCellSize.ToString().Substring(1));
00194                     float ipd = target.StereoIpdMm / 1000.0f;
00195                     int vTotal = halfHeight / cellSize;
00196                     float dv = 90.0f / vTotal; // vertical degrees per segment
00197                     float dvHalf = dv / 2.0f;
00198 
00199                     var targetTexture = new RenderTexture(cellSize, cellSize, 24);
00200                     targetTexture.wrapMode = TextureWrapMode.Clamp;
00201                     targetTexture.antiAliasing = 8;
00202 
00203                     camera.fieldOfView = dv;
00204                     camera.orthographic = false;
00205                     camera.targetTexture = targetTexture;
00206 
00207                     // Render sections of a sphere using a rectilinear projection
00208                     // and resample using a sphereical projection into a single panorama
00209                     // texture per eye.  We break into sections in order to keep the eye
00210                     // separation similar around the sphere.  Rendering alternates between
00211                     // top and bottom sections, sweeping horizontally around the sphere,
00212                     // alternating left and right eyes.
00213                     for (int v = 0; v < vTotal; v++)
00214                     {
00215                         var pitch = 90.0f - (v * dv) - dvHalf;
00216                         var uTotal = width / targetTexture.width;
00217                         var du = 360.0f / uTotal; // horizontal degrees per segment
00218                         var duHalf = du / 2.0f;
00219 
00220                         var vTarget = v * halfHeight / vTotal;
00221 
00222                         for (int i = 0; i < 2; i++) // top, bottom
00223                         {
00224                             if (i == 1)
00225                             {
00226                                 pitch = -pitch;
00227                                 vTarget = height - vTarget - cellSize;
00228                             }
00229 
00230                             for (int u = 0; u < uTotal; u++)
00231                             {
00232                                 var yaw = -180.0f + (u * du) + duHalf;
00233 
00234                                 var uTarget = u * width / uTotal;
00235 
00236                                 var xOffset = -ipd / 2 * Mathf.Cos(pitch * Mathf.Deg2Rad);
00237 
00238                                 for (int j = 0; j < 2; j++) // left, right
00239                                 {
00240                                     var texture = textures[j];
00241 
00242                                     if (j == 1)
00243                                     {
00244                                         xOffset = -xOffset;
00245                                     }
00246 
00247                                     var offset = baseRotation * Quaternion.Euler(0, yaw, 0) * new Vector3(xOffset, 0, 0);
00248                                     transform.position = basePosition + offset;
00249 
00250                                     var direction = Quaternion.Euler(pitch, yaw, 0.0f);
00251                                     transform.rotation = baseRotation * direction;
00252 
00253                                     // vector pointing to center of this section
00254                                     var N = direction * Vector3.forward;
00255 
00256                                     // horizontal span of this section in degrees
00257                                     var phi0 = yaw - (du / 2);
00258                                     var phi1 = phi0 + du;
00259 
00260                                     // vertical span of this section in degrees
00261                                     var theta0 = pitch + (dv / 2);
00262                                     var theta1 = theta0 - dv;
00263 
00264                                     var midPhi = (phi0 + phi1) / 2;
00265                                     var baseTheta = Mathf.Abs(theta0) < Mathf.Abs(theta1) ? theta0 : theta1;
00266 
00267                                     // vectors pointing to corners of image closes to the equator
00268                                     var V00 = Quaternion.Euler(baseTheta, phi0, 0.0f) * Vector3.forward;
00269                                     var V01 = Quaternion.Euler(baseTheta, phi1, 0.0f) * Vector3.forward;
00270 
00271                                     // vectors pointing to top and bottom midsection of image
00272                                     var V0M = Quaternion.Euler(theta0, midPhi, 0.0f) * Vector3.forward;
00273                                     var V1M = Quaternion.Euler(theta1, midPhi, 0.0f) * Vector3.forward;
00274 
00275                                     // intersection points for each of the above
00276                                     var P00 = V00 / Vector3.Dot(V00, N);
00277                                     var P01 = V01 / Vector3.Dot(V01, N);
00278                                     var P0M = V0M / Vector3.Dot(V0M, N);
00279                                     var P1M = V1M / Vector3.Dot(V1M, N);
00280 
00281                                     // calculate basis vectors for plane
00282                                     var P00_P01 = P01 - P00;
00283                                     var P0M_P1M = P1M - P0M;
00284 
00285                                     var uMag = P00_P01.magnitude;
00286                                     var vMag = P0M_P1M.magnitude;
00287 
00288                                     var uScale = 1.0f / uMag;
00289                                     var vScale = 1.0f / vMag;
00290 
00291                                     var uAxis = P00_P01 * uScale;
00292                                     var vAxis = P0M_P1M * vScale;
00293 
00294                                     // update material constant buffer
00295                                     fx.Set(N, phi0, phi1, theta0, theta1,
00296                                         uAxis, P00, uScale,
00297                                         vAxis, P0M, vScale);
00298 
00299                                     camera.aspect = uMag / vMag;
00300                                     camera.Render();
00301 
00302                                     RenderTexture.active = targetTexture;
00303                                     texture.ReadPixels(new Rect(0, 0, targetTexture.width, targetTexture.height), uTarget, vTarget);
00304                                     RenderTexture.active = null;
00305                                 }
00306                             }
00307                         }
00308                     }
00309 
00310                     // Save textures to disk.
00311                     for (int i = 0; i < 2; i++)
00312                     {
00313                         var texture = textures[i];
00314 
00315                         texture.Apply();
00316                         var assetName = string.Format(nameFormat, assetPath, target.name, i);
00317                         File.WriteAllBytes(assetName, texture.EncodeToPNG());
00318                     }
00319 
00320                     // Cleanup.
00321                     if (camera != tempCamera)
00322                     {
00323                         camera.targetTexture = oldTargetTexture;
00324                         camera.orthographic = oldOrthographic;
00325                         camera.fieldOfView = oldFieldOfView;
00326                         camera.aspect = oldAspect;
00327 
00328                         target.transform.localPosition = oldPosition;
00329                         target.transform.localRotation = oldRotation;
00330                     }
00331                     else
00332                     {
00333                         tempCamera.targetTexture = null;
00334                     }
00335 
00336                     DestroyImmediate(targetTexture);
00337                     DestroyImmediate(fx);
00338 
00339                     timer.Stop();
00340                     Debug.Log(string.Format("<b>[SteamVR]</b> Screenshot took {0} seconds.", timer.Elapsed));
00341                 }
00342 
00343                 if (tempCamera != null)
00344                 {
00345                     DestroyImmediate(tempCamera.gameObject);
00346                 }
00347 
00348                 DestroyImmediate(textures[0]);
00349                 DestroyImmediate(textures[1]);
00350 
00351                 // Now that everything has be written out, reload the associated assets and assign them.
00352                 AssetDatabase.Refresh();
00353                 foreach (SteamVR_Skybox target in targets)
00354                 {
00355                     var targetScene = target.gameObject.scene;
00356                     var sceneName = Path.GetFileNameWithoutExtension(targetScene.path);
00357                     var scenePath = Path.GetDirectoryName(targetScene.path);
00358                     var assetPath = scenePath + "/" + sceneName;
00359 
00360                     for (int i = 0; i < 2; i++)
00361                     {
00362                         var assetName = string.Format(nameFormat, assetPath, target.name, i);
00363                         var importer = AssetImporter.GetAtPath(assetName) as TextureImporter;
00364                         importer.mipmapEnabled = false;
00365                         importer.wrapMode = TextureWrapMode.Repeat;
00366 #if (UNITY_5_4 || UNITY_5_3 || UNITY_5_2 || UNITY_5_1 || UNITY_5_0)
00367                         importer.SetPlatformTextureSettings("Standalone", width, TextureImporterFormat.RGB24);
00368 #else
00369                     var settings = importer.GetPlatformTextureSettings("Standalone");
00370                     settings.textureCompression = TextureImporterCompression.Uncompressed;
00371                     settings.maxTextureSize = width;
00372                     importer.SetPlatformTextureSettings(settings);
00373 #endif
00374                         importer.SaveAndReimport();
00375 
00376                         var texture = AssetDatabase.LoadAssetAtPath<Texture2D>(assetName);
00377                         target.SetTextureByIndex(i, texture);
00378                     }
00379                 }
00380             }
00381         }
00382     }
