00069         static void Update()
00070         {
00071             bool show =
00072                 (!EditorPrefs.HasKey(ignore + buildTarget) &&
00073                     EditorUserBuildSettings.activeBuildTarget != recommended_BuildTarget) ||
00074                 (!EditorPrefs.HasKey(ignore + showUnitySplashScreen) &&
00075 #if (UNITY_5_4 || UNITY_5_3 || UNITY_5_2 || UNITY_5_1 || UNITY_5_0)
00076                 PlayerSettings.showUnitySplashScreen != recommended_ShowUnitySplashScreen) ||
00077 #else
00078                 PlayerSettings.SplashScreen.show != recommended_ShowUnitySplashScreen) ||
00079 #endif
00080 
00081 #if UNITY_2018_1_OR_NEWER
00082             (!EditorPrefs.HasKey(ignore + defaultIsFullScreen) &&
00083                 PlayerSettings.fullScreenMode != recommended_FullScreenMode) ||
00084 #else
00085             (!EditorPrefs.HasKey(ignore + defaultIsFullScreen) &&
00086                     PlayerSettings.defaultIsFullScreen != recommended_DefaultIsFullScreen) ||
00087                 (!EditorPrefs.HasKey(ignore + fullscreenMode) &&
00088                     PlayerSettings.d3d11FullscreenMode != recommended_FullscreenMode) ||
00089 #endif
00090             (!EditorPrefs.HasKey(ignore + defaultScreenSize) &&
00091                     (PlayerSettings.defaultScreenWidth != recommended_DefaultScreenWidth ||
00092                     PlayerSettings.defaultScreenHeight != recommended_DefaultScreenHeight)) ||
00093                 (!EditorPrefs.HasKey(ignore + runInBackground) &&
00094                     PlayerSettings.runInBackground != recommended_RunInBackground) ||
00095                 (!EditorPrefs.HasKey(ignore + displayResolutionDialog) &&
00096                     PlayerSettings.displayResolutionDialog != recommended_DisplayResolutionDialog) ||
00097                 (!EditorPrefs.HasKey(ignore + resizableWindow) &&
00098                     PlayerSettings.resizableWindow != recommended_ResizableWindow) ||
00099                 (!EditorPrefs.HasKey(ignore + visibleInBackground) &&
00100                     PlayerSettings.visibleInBackground != recommended_VisibleInBackground) ||
00101 #if (UNITY_5_4 || UNITY_5_3 || UNITY_5_2 || UNITY_5_1 || UNITY_5_0)
00102             (!EditorPrefs.HasKey(ignore + renderingPath) &&
00103                     PlayerSettings.renderingPath != recommended_RenderPath) ||
00104 #endif
00105             (!EditorPrefs.HasKey(ignore + colorSpace) &&
00106                     PlayerSettings.colorSpace != recommended_ColorSpace) ||
00107                 (!EditorPrefs.HasKey(ignore + gpuSkinning) &&
00108                     PlayerSettings.gpuSkinning != recommended_GpuSkinning) ||
00109 #if false
00110             (!EditorPrefs.HasKey(ignore + singlePassStereoRendering) &&
00111                 PlayerSettings.singlePassStereoRendering != recommended_SinglePassStereoRendering) ||
00112 #endif
00113             forceShow;
00114 
00115             if (show)
00116             {
00117                 window = GetWindow<SteamVR_UnitySettingsWindow>(true);
00118                 window.minSize = new Vector2(320, 440);
00119                 //window.title = "SteamVR";
00120             }
00121 
00122             string[] dlls = new string[]
00123             {
00124             "Plugins/x86/openvr_api.dll",
00125             "Plugins/x86_64/openvr_api.dll"
00126             };
00127 
00128             foreach (string path in dlls)
00129             {
00130                 if (!File.Exists(Application.dataPath + "/" + path))
00131                     continue;
00132 
00133                 if (AssetDatabase.DeleteAsset("Assets/" + path))
00134                     Debug.Log("<b>[SteamVR Setup]</b> Deleting " + path);
00135                 else
00136                 {
00137                     Debug.Log("<b>[SteamVR Setup]</b> " + path + " in use; cannot delete.  Please restart Unity to complete upgrade.");
00138                 }
00139             }
00140 
00141             EditorApplication.update -= Update;
00142         }
