00146         string GetResourcePath()
00147         {
00148             var ms = MonoScript.FromScriptableObject(this);
00149             var path = AssetDatabase.GetAssetPath(ms);
00150             path = Path.GetDirectoryName(path);
00151             return path.Substring(0, path.Length - "Editor".Length) + "Textures/";
00152         }
