00019         // this version does not clamp [0..1]
00020         public static Quaternion Slerp(Quaternion A, Quaternion B, float time)
00021         {
00022             float cosom = Mathf.Clamp(A.x * B.x + A.y * B.y + A.z * B.z + A.w * B.w, -1.0f, 1.0f);
00023             if (cosom < 0.0f)
00024             {
00025                 B = new Quaternion(-B.x, -B.y, -B.z, -B.w);
00026                 cosom = -cosom;
00027             }
00028 
00029             float sclp, sclq;
00030             if ((1.0f - cosom) > 0.0001f)
00031             {
00032                 float omega = Mathf.Acos(cosom);
00033                 float sinom = Mathf.Sin(omega);
00034                 sclp = Mathf.Sin((1.0f - time) * omega) / sinom;
00035                 sclq = Mathf.Sin(time * omega) / sinom;
00036             }
00037             else
00038             {
00039                 // "from" and "to" very close, so do linear interp
00040                 sclp = 1.0f - time;
00041                 sclq = time;
00042             }
00043 
00044             return new Quaternion(
00045                 sclp * A.x + sclq * B.x,
00046                 sclp * A.y + sclq * B.y,
00047                 sclp * A.z + sclq * B.z,
00048                 sclp * A.w + sclq * B.w);
00049         }
