00119         public static Quaternion GetRotation(this Matrix4x4 matrix)
00120         {
00121             Quaternion q = new Quaternion();
00122             q.w = Mathf.Sqrt(Mathf.Max(0, 1f + matrix.m00 + matrix.m11 + matrix.m22)) / 2f;
00123             q.x = Mathf.Sqrt(Mathf.Max(0, 1f + matrix.m00 - matrix.m11 - matrix.m22)) / 2f;
00124             q.y = Mathf.Sqrt(Mathf.Max(0, 1f - matrix.m00 + matrix.m11 - matrix.m22)) / 2f;
00125             q.z = Mathf.Sqrt(Mathf.Max(0, 1f - matrix.m00 - matrix.m11 + matrix.m22)) / 2f;
00126             q.x = _copysign(q.x, matrix.m21 - matrix.m12);
00127             q.y = _copysign(q.y, matrix.m02 - matrix.m20);
00128             q.z = _copysign(q.z, matrix.m10 - matrix.m01);
00129             return q;
00130         }
