00141         public static Vector3 GetScale(this Matrix4x4 m)
00142         {
00143             float x = Mathf.Sqrt(m.m00 * m.m00 + m.m01 * m.m01 + m.m02 * m.m02);
00144             float y = Mathf.Sqrt(m.m10 * m.m10 + m.m11 * m.m11 + m.m12 * m.m12);
00145             float z = Mathf.Sqrt(m.m20 * m.m20 + m.m21 * m.m21 + m.m22 * m.m22);
00146 
00147             return new Vector3(x, y, z);
00148         }
