00256                 pose.m2 = -matrix[0, 2];
00257                 pose.m3 = matrix[0, 3];
00258 
00259                 pose.m4 = matrix[1, 0];
00260                 pose.m5 = matrix[1, 1];
00261                 pose.m6 = -matrix[1, 2];
00262                 pose.m7 = matrix[1, 3];
00263 
00264                 pose.m8 = -matrix[2, 0];
00265                 pose.m9 = -matrix[2, 1];
00266                 pose.m10 = matrix[2, 2];
00267                 pose.m11 = -matrix[2, 3];
00268 
00269                 pose.m12 = matrix[3, 0];
00270                 pose.m13 = matrix[3, 1];
00271                 pose.m14 = -matrix[3, 2];
00272                 pose.m15 = matrix[3, 3];
00273 
00274                 return pose;
00275             }
