00400         public static void TakeStereoScreenshot(uint screenshotHandle, GameObject target, int cellSize, float ipd, ref string previewFilename, ref string VRFilename)
00401         {
00402             const int width = 4096;
00403             const int height = width / 2;
00404             const int halfHeight = height / 2;
00405 
00406             Texture2D texture = new Texture2D(width, height * 2, TextureFormat.ARGB32, false);
00407 
00408             System.Diagnostics.Stopwatch timer = new System.Diagnostics.Stopwatch();
00409 
00410             Camera tempCamera = null;
00411 
00412             timer.Start();
00413 
00414             Camera camera = target.GetComponent<Camera>();
00415             if (camera == null)
00416             {
00417                 if (tempCamera == null)
00418                     tempCamera = new GameObject().AddComponent<Camera>();
00419                 camera = tempCamera;
00420             }
00421 
00422             // Render preview texture
00423             const int previewWidth = 2048;
00424             const int previewHeight = 2048;
00425             Texture2D previewTexture = new Texture2D(previewWidth, previewHeight, TextureFormat.ARGB32, false);
00426             RenderTexture targetPreviewTexture = new RenderTexture(previewWidth, previewHeight, 24);
00427 
00428             RenderTexture oldTargetTexture = camera.targetTexture;
00429             bool oldOrthographic = camera.orthographic;
00430             float oldFieldOfView = camera.fieldOfView;
00431             float oldAspect = camera.aspect;
00432             StereoTargetEyeMask oldstereoTargetEye = camera.stereoTargetEye;
00433             camera.stereoTargetEye = StereoTargetEyeMask.None;
00434             camera.fieldOfView = 60.0f;
00435             camera.orthographic = false;
00436             camera.targetTexture = targetPreviewTexture;
00437             camera.aspect = 1.0f;
00438             camera.Render();
00439 
00440             // copy preview texture
00441             RenderTexture.active = targetPreviewTexture;
00442             previewTexture.ReadPixels(new Rect(0, 0, targetPreviewTexture.width, targetPreviewTexture.height), 0, 0);
00443             RenderTexture.active = null;
00444             camera.targetTexture = null;
00445             Object.DestroyImmediate(targetPreviewTexture);
00446 
00447             SteamVR_SphericalProjection fx = camera.gameObject.AddComponent<SteamVR_SphericalProjection>();
00448 
00449             Vector3 oldPosition = target.transform.localPosition;
00450             Quaternion oldRotation = target.transform.localRotation;
00451             Vector3 basePosition = target.transform.position;
00452             Quaternion baseRotation = Quaternion.Euler(0, target.transform.rotation.eulerAngles.y, 0);
00453 
00454             Transform transform = camera.transform;
00455 
00456             int vTotal = halfHeight / cellSize;
00457             float dv = 90.0f / vTotal; // vertical degrees per segment
00458             float dvHalf = dv / 2.0f;
00459 
00460             RenderTexture targetTexture = new RenderTexture(cellSize, cellSize, 24);
00461             targetTexture.wrapMode = TextureWrapMode.Clamp;
00462             targetTexture.antiAliasing = 8;
00463 
00464             camera.fieldOfView = dv;
00465             camera.orthographic = false;
00466             camera.targetTexture = targetTexture;
00467             camera.aspect = oldAspect;
00468             camera.stereoTargetEye = StereoTargetEyeMask.None;
00469 
00470             // Render sections of a sphere using a rectilinear projection
00471             // and resample using a sphereical projection into a single panorama
00472             // texture per eye.  We break into sections in order to keep the eye
00473             // separation similar around the sphere.  Rendering alternates between
00474             // top and bottom sections, sweeping horizontally around the sphere,
00475             // alternating left and right eyes.
00476             for (int v = 0; v < vTotal; v++)
00477             {
00478                 float pitch = 90.0f - (v * dv) - dvHalf;
00479                 int uTotal = width / targetTexture.width;
00480                 float du = 360.0f / uTotal; // horizontal degrees per segment
00481                 float duHalf = du / 2.0f;
00482 
00483                 int vTarget = v * halfHeight / vTotal;
00484 
00485                 for (int i = 0; i < 2; i++) // top, bottom
00486                 {
00487                     if (i == 1)
00488                     {
00489                         pitch = -pitch;
00490                         vTarget = height - vTarget - cellSize;
00491                     }
00492 
00493                     for (int u = 0; u < uTotal; u++)
00494                     {
00495                         float yaw = -180.0f + (u * du) + duHalf;
00496 
00497                         int uTarget = u * width / uTotal;
00498 
00499                         int vTargetOffset = 0;
00500                         float xOffset = -ipd / 2 * Mathf.Cos(pitch * Mathf.Deg2Rad);
00501 
00502                         for (int j = 0; j < 2; j++) // left, right
00503                         {
00504                             if (j == 1)
00505                             {
00506                                 vTargetOffset = height;
00507                                 xOffset = -xOffset;
00508                             }
00509 
00510                             Vector3 offset = baseRotation * Quaternion.Euler(0, yaw, 0) * new Vector3(xOffset, 0, 0);
00511                             transform.position = basePosition + offset;
00512 
00513                             Quaternion direction = Quaternion.Euler(pitch, yaw, 0.0f);
00514                             transform.rotation = baseRotation * direction;
00515 
00516                             // vector pointing to center of this section
00517                             Vector3 N = direction * Vector3.forward;
00518 
00519                             // horizontal span of this section in degrees
00520                             float phi0 = yaw - (du / 2);
00521                             float phi1 = phi0 + du;
00522 
00523                             // vertical span of this section in degrees
00524                             float theta0 = pitch + (dv / 2);
00525                             float theta1 = theta0 - dv;
00526 
00527                             float midPhi = (phi0 + phi1) / 2;
00528                             float baseTheta = Mathf.Abs(theta0) < Mathf.Abs(theta1) ? theta0 : theta1;
00529 
00530                             // vectors pointing to corners of image closes to the equator
00531                             Vector3 V00 = Quaternion.Euler(baseTheta, phi0, 0.0f) * Vector3.forward;
00532                             Vector3 V01 = Quaternion.Euler(baseTheta, phi1, 0.0f) * Vector3.forward;
00533 
00534                             // vectors pointing to top and bottom midsection of image
00535                             Vector3 V0M = Quaternion.Euler(theta0, midPhi, 0.0f) * Vector3.forward;
00536                             Vector3 V1M = Quaternion.Euler(theta1, midPhi, 0.0f) * Vector3.forward;
00537 
00538                             // intersection points for each of the above
00539                             Vector3 P00 = V00 / Vector3.Dot(V00, N);
00540                             Vector3 P01 = V01 / Vector3.Dot(V01, N);
00541                             Vector3 P0M = V0M / Vector3.Dot(V0M, N);
00542                             Vector3 P1M = V1M / Vector3.Dot(V1M, N);
00543 
00544                             // calculate basis vectors for plane
00545                             Vector3 P00_P01 = P01 - P00;
00546                             Vector3 P0M_P1M = P1M - P0M;
00547 
00548                             float uMag = P00_P01.magnitude;
00549                             float vMag = P0M_P1M.magnitude;
00550 
00551                             float uScale = 1.0f / uMag;
00552                             float vScale = 1.0f / vMag;
00553 
00554                             Vector3 uAxis = P00_P01 * uScale;
00555                             Vector3 vAxis = P0M_P1M * vScale;
00556 
00557                             // update material constant buffer
00558                             fx.Set(N, phi0, phi1, theta0, theta1,
00559                                 uAxis, P00, uScale,
00560                                 vAxis, P0M, vScale);
00561 
00562                             camera.aspect = uMag / vMag;
00563                             camera.Render();
00564 
00565                             RenderTexture.active = targetTexture;
00566                             texture.ReadPixels(new Rect(0, 0, targetTexture.width, targetTexture.height), uTarget, vTarget + vTargetOffset);
00567                             RenderTexture.active = null;
00568                         }
00569 
00570                         // Update progress
00571                         float progress = (float)(v * (uTotal * 2.0f) + u + i * uTotal) / (float)(vTotal * (uTotal * 2.0f));
00572                         OpenVR.Screenshots.UpdateScreenshotProgress(screenshotHandle, progress);
00573                     }
00574                 }
00575             }
00576 
00577             // 100% flush
00578             OpenVR.Screenshots.UpdateScreenshotProgress(screenshotHandle, 1.0f);
00579 
00580             // Save textures to disk.
00581             // Add extensions
00582             previewFilename += ".png";
00583             VRFilename += ".png";
00584 
00585             // Preview
00586             previewTexture.Apply();
00587             System.IO.File.WriteAllBytes(previewFilename, previewTexture.EncodeToPNG());
00588 
00589             // VR
00590             texture.Apply();
00591             System.IO.File.WriteAllBytes(VRFilename, texture.EncodeToPNG());
00592 
00593             // Cleanup.
00594             if (camera != tempCamera)
00595             {
00596                 camera.targetTexture = oldTargetTexture;
00597                 camera.orthographic = oldOrthographic;
00598                 camera.fieldOfView = oldFieldOfView;
00599                 camera.aspect = oldAspect;
00600                 camera.stereoTargetEye = oldstereoTargetEye;
00601 
00602                 target.transform.localPosition = oldPosition;
00603                 target.transform.localRotation = oldRotation;
00604             }
00605             else
00606             {
00607                 tempCamera.targetTexture = null;
00608             }
00609 
00610             Object.DestroyImmediate(targetTexture);
00611             Object.DestroyImmediate(fx);
00612 
00613             timer.Stop();
00614             Debug.Log(string.Format("<b>[SteamVR]</b> Screenshot took {0} seconds.", timer.Elapsed));
00615 
00616             if (tempCamera != null)
00617             {
00618                 Object.DestroyImmediate(tempCamera.gameObject);
00619             }
00620 
00621             Object.DestroyImmediate(previewTexture);
00622             Object.DestroyImmediate(texture);
00623         }
