00633         public static string GetBadMD5Hash(byte[] bytes)
00634         {
00635             System.Security.Cryptography.MD5CryptoServiceProvider md5 = new System.Security.Cryptography.MD5CryptoServiceProvider();
00636             byte[] hash = md5.ComputeHash(bytes);
00637 
00638             System.Text.StringBuilder sb = new System.Text.StringBuilder();
00639             for (int i = 0; i < hash.Length; i++)
00640             {
00641                 sb.Append(hash[i].ToString("x2"));
00642             }
00643 
00644             return sb.ToString();
00645         }
00646         public static string GetBadMD5HashFromFile(string filePath)
00647         {
00648             if (File.Exists(filePath) == false)
00649                 return null;
00650 
00651             string data = File.ReadAllText(filePath);
