00663         public static float GetLossyScale(Transform forTransform)
00664         {
00665             float scale = 1f;
00666             while (forTransform != null && forTransform.parent != null)
00667             {
00668                 forTransform = forTransform.parent;
00669                 scale *= forTransform.localScale.x;
