00685         private static Dictionary<int, GameObject> velocityCache = new Dictionary<int, GameObject>();
00686         public static void DrawVelocity(int key, Vector3 position, Vector3 velocity, float destroyAfterSeconds = 5f)
00687         {
00688             DrawVelocity(key, position, velocity, Color.green, destroyAfterSeconds);
00689         }
00690         public static void DrawVelocity(int key, Vector3 position, Vector3 velocity, Color color, float destroyAfterSeconds = 5f)
00691         {
00692             if (velocityCache.ContainsKey(key) == false || velocityCache[key] == null)
00693             {
00694                 GameObject center = GameObject.CreatePrimitive(PrimitiveType.Cube);
00695                 center.transform.localScale = Vector3.one * 0.025f;
00696                 center.transform.position = position;
00697 
00698                 if (velocity != Vector3.zero)
00699                     center.transform.forward = velocity;
00700 
00701                 GameObject arrow = GameObject.CreatePrimitive(PrimitiveType.Cube);
00702                 arrow.transform.parent = center.transform;
00703 
00704                 if (velocity != Vector3.zero)
00705                 {
00706                     arrow.transform.localScale = new Vector3(0.25f, 0.25f, 3 + (velocity.magnitude * 1.5f));
00707                     arrow.transform.localPosition = new Vector3(0, 0, arrow.transform.localScale.z / 2f);
00708                 }
00709                 else
00710                 {
00711                     arrow.transform.localScale = Vector3.one;
00712                     arrow.transform.localPosition = Vector3.zero;
00713                 }
00714                 arrow.transform.localRotation = Quaternion.identity;
00715 
00716                 GameObject.DestroyImmediate(arrow.GetComponent<Collider>());
00717                 GameObject.DestroyImmediate(center.GetComponent<Collider>());
00718 
00719                 center.GetComponent<MeshRenderer>().material.color = color;
00720                 arrow.GetComponent<MeshRenderer>().material.color = color;
00721 
00722                 velocityCache[key] = center;
00723 
00724                 GameObject.Destroy(center, destroyAfterSeconds);
00725             }
00726             else
00727             {
00728                 GameObject center = velocityCache[key];
00729                 center.transform.position = position;
00730 
00731                 if (velocity != Vector3.zero)
00732                     center.transform.forward = velocity;
00733 
00734                 Transform arrow = center.transform.GetChild(0);
00735 
00736                 if (velocity != Vector3.zero)
00737                 {
00738                     arrow.localScale = new Vector3(0.25f, 0.25f, 3 + (velocity.magnitude * 1.5f));
00739                     arrow.localPosition = new Vector3(0, 0, arrow.transform.localScale.z / 2f);
00740                 }
00741                 else
00742                 {
00743                     arrow.localScale = Vector3.one;
00744                     arrow.localPosition = Vector3.zero;
00745                 }
00746                 arrow.localRotation = Quaternion.identity;
00747 
00748                 GameObject.Destroy(center, destroyAfterSeconds);
00749             }
00750         }
