00010 using System.IO;
00011 using UnityEditor;
00012 
00013 namespace Leap.Unity {
00014 
00015   [CustomPropertyDrawer(typeof(StreamingFolder), useForChildren: true)]
00016   public class StreamingAssetPropertyDrawer : StreamingFolderPropertyDrawer {
00017 
00018     protected override string PromptUserForPath(string currentPath) {
00019       return EditorUtility.OpenFilePanel("Select File", currentPath, "");
00020     }
00021 
00022     protected override bool ValidatePath(string fullPath, string relativePath, out string errorMessage) {
00023       if (!File.Exists(fullPath)) {
00024         errorMessage = "The specified file does not exist!";
00025         return false;
00026       }
00027 
00028       if ((File.GetAttributes(fullPath) & FileAttributes.Directory) == FileAttributes.Directory) {
00029         errorMessage = "You must specify a file and not a directory!";
00030         return false;
00031       }
00032 
00033       bool isValid = base.ValidatePath(fullPath, relativePath, out errorMessage);
00034       if (!isValid) {
00035         errorMessage = "The specified file is not a streaming asset. Streaming assets must be inside project's Assets/StreamingAssets directory.";
00036       }
00037       return isValid;
00038     }
00039   }
00040 }
