00010 using System;
00011 using System.Linq;
00012 using UnityEngine;
00013 #if UNITY_EDITOR
00014 using UnityEditor;
00015 #endif
00016 
00017 namespace Leap.Unity {
00018 
00019   [Serializable]
00020   public class StreamingFolder : AssetFolder, ISerializationCallbackReceiver {
00021 
00022     [SerializeField]
00023     private string _relativePath;
00024 
00031     public override string Path {
00032       get {
00033         return System.IO.Path.Combine(Application.streamingAssetsPath, _relativePath);
00034       }
00035       set {
00036         throw new InvalidOperationException();
00037       }
00038     }
00039 
00040     public void OnAfterDeserialize() { }
00041 
00042     public void OnBeforeSerialize() {
00043 #if UNITY_EDITOR
00044       string assetPath = AssetDatabase.GetAssetPath(_assetFolder);
00045       if (string.IsNullOrEmpty(assetPath)) {
00046         _relativePath = null;
00047       } else {
00048         string fullFolder = System.IO.Path.GetFullPath(assetPath);
00049         _relativePath = Utils.MakeRelativePath(Application.streamingAssetsPath, fullFolder);
00050         _relativePath = string.Join(System.IO.Path.DirectorySeparatorChar.ToString(),
00051                                     _relativePath.Split(System.IO.Path.DirectorySeparatorChar).Skip(1).ToArray());
00052       }
00053 #endif
00054     }
00055   }
00056 }
