00282         public void OnSceneGUI()
00283         {
00284             if (IsMixSelectionTypes()) return;
00285 
00286             // Margin Frame & Handles
00287             m_RectTransform.GetWorldCorners(m_RectCorners);
00288             Vector4 marginOffset = m_TextComponent.margin;
00289             Vector3 lossyScale = m_RectTransform.lossyScale;
00290             
00291             m_HandlePoints[0] = m_RectCorners[0] + m_RectTransform.TransformDirection(new Vector3(marginOffset.x * lossyScale.x, marginOffset.w * lossyScale.y, 0));
00292             m_HandlePoints[1] = m_RectCorners[1] + m_RectTransform.TransformDirection(new Vector3(marginOffset.x * lossyScale.x, -marginOffset.y * lossyScale.y, 0));
00293             m_HandlePoints[2] = m_RectCorners[2] + m_RectTransform.TransformDirection(new Vector3(-marginOffset.z * lossyScale.x, -marginOffset.y * lossyScale.y, 0));
00294             m_HandlePoints[3] = m_RectCorners[3] + m_RectTransform.TransformDirection(new Vector3(-marginOffset.z * lossyScale.x, marginOffset.w * lossyScale.y, 0));
00295 
00296             Handles.DrawSolidRectangleWithOutline(m_HandlePoints, new Color32(255, 255, 255, 0), new Color32(255, 255, 0, 255));
00297             
00298             // Draw & process FreeMoveHandles
00299 
00300             // LEFT HANDLE
00301             Vector3 oldLeft = (m_HandlePoints[0] + m_HandlePoints[1]) * 0.5f;
00302             Vector3 newLeft = Handles.FreeMoveHandle(oldLeft, Quaternion.identity, HandleUtility.GetHandleSize(m_RectTransform.position) * 0.05f, Vector3.zero, Handles.DotHandleCap);
00303             bool hasChanged = false;
00304             if (oldLeft != newLeft)
00305             {
00306                 float delta = oldLeft.x - newLeft.x;
00307                 marginOffset.x += -delta / lossyScale.x;
00308                 //Debug.Log("Left Margin H0:" + handlePoints[0] + "  H1:" + handlePoints[1]);
00309                 hasChanged = true;
00310             }
00311 
00312             // TOP HANDLE
00313             Vector3 oldTop = (m_HandlePoints[1] + m_HandlePoints[2]) * 0.5f;
00314             Vector3 newTop = Handles.FreeMoveHandle(oldTop, Quaternion.identity, HandleUtility.GetHandleSize(m_RectTransform.position) * 0.05f, Vector3.zero, Handles.DotHandleCap);
00315             if (oldTop != newTop)
00316             {
00317                 float delta = oldTop.y - newTop.y;
00318                 marginOffset.y += delta / lossyScale.y;
00319                 //Debug.Log("Top Margin H1:" + handlePoints[1] + "  H2:" + handlePoints[2]);   
00320                 hasChanged = true;
00321             }
00322 
00323             // RIGHT HANDLE
00324             Vector3 oldRight = (m_HandlePoints[2] + m_HandlePoints[3]) * 0.5f;
00325             Vector3 newRight = Handles.FreeMoveHandle(oldRight, Quaternion.identity, HandleUtility.GetHandleSize(m_RectTransform.position) * 0.05f, Vector3.zero, Handles.DotHandleCap);
00326             if (oldRight != newRight)
00327             {
00328                 float delta = oldRight.x - newRight.x;
00329                 marginOffset.z += delta / lossyScale.x;
00330                 hasChanged = true;
00331                 //Debug.Log("Right Margin H2:" + handlePoints[2] + "  H3:" + handlePoints[3]);
00332             }
00333 
00334             // BOTTOM HANDLE
00335             Vector3 oldBottom = (m_HandlePoints[3] + m_HandlePoints[0]) * 0.5f;
00336             Vector3 newBottom = Handles.FreeMoveHandle(oldBottom, Quaternion.identity, HandleUtility.GetHandleSize(m_RectTransform.position) * 0.05f, Vector3.zero, Handles.DotHandleCap);
00337             if (oldBottom != newBottom)
00338             {
00339                 float delta = oldBottom.y - newBottom.y;
00340                 marginOffset.w += -delta / lossyScale.y;
00341                 hasChanged = true;
00342                 //Debug.Log("Bottom Margin H0:" + handlePoints[0] + "  H3:" + handlePoints[3]);
00343             }
00344 
00345             if (hasChanged)
00346             {
00347                 Undo.RecordObjects(new Object[] {m_RectTransform, m_TextComponent }, "Margin Changes");
00348                 m_TextComponent.margin = marginOffset;
00349                 EditorUtility.SetDirty(target);
00350             }
00351         }
