00353         protected void DrawTextInput()
00354         {
00355             EditorGUILayout.Space();
00356 
00357             // If the text component is linked, disable the text input box.
00358             if (m_IsLinkedTextComponentProp.boolValue)
00359             {
00360                 EditorGUILayout.HelpBox("The Text Input Box is disabled due to this text component being linked to another.", MessageType.Info);
00361             }
00362             else
00363             {
00364                 EditorGUI.BeginChangeCheck();
00365                 EditorGUILayout.PropertyField(m_TextProp);
00366 
00367                 if (EditorGUI.EndChangeCheck() || (m_IsRightToLeftProp.boolValue && string.IsNullOrEmpty(m_RtlText)))
00368                 {
00369                     m_InputSourceProp.enumValueIndex = 0;
00370                     m_IsInputPasingRequiredProp.boolValue = true;
00371                     m_HavePropertiesChanged = true;
00372 
00373                     // Handle Left to Right or Right to Left Editor
00374                     if (m_IsRightToLeftProp.boolValue)
00375                     {
00376                         m_RtlText = string.Empty;
00377                         string sourceText = m_TextProp.stringValue;
00378 
00379                         // Reverse Text displayed in Text Input Box
00380                         for (int i = 0; i < sourceText.Length; i++)
00381                         {
00382                             m_RtlText += sourceText[sourceText.Length - i - 1];
00383                         }
00384                     }
00385                 }
00386 
00387                 // Toggle showing Rich Tags
00388                 m_IsRightToLeftProp.boolValue = EditorGUILayout.Toggle(k_RtlToggleLabel, m_IsRightToLeftProp.boolValue);
00389 
00390                 if (m_IsRightToLeftProp.boolValue)
00391                 {
00392                     EditorGUI.BeginChangeCheck();
00393                     m_RtlText = EditorGUILayout.TextArea(m_RtlText, TMP_UIStyleManager.wrappingTextArea, GUILayout.Height(EditorGUI.GetPropertyHeight(m_TextProp) - EditorGUIUtility.singleLineHeight), GUILayout.ExpandWidth(true));
00394                     if (EditorGUI.EndChangeCheck())
00395                     {
00396                         // Convert RTL input
00397                         string sourceText = string.Empty;
00398 
00399                         // Reverse Text displayed in Text Input Box
00400                         for (int i = 0; i < m_RtlText.Length; i++)
00401                         {
00402                             sourceText += m_RtlText[m_RtlText.Length - i - 1];
00403                         }
00404 
00405                         m_TextProp.stringValue = sourceText;
00406                     }
00407                 }
00408             }
00409         }
