00450 
00451             Rect rect;
00452 
00453             // MATERIAL PRESET
00454             if (m_MaterialPresetNames != null)
00455             {
00456                 EditorGUI.BeginChangeCheck();
00457                 rect = EditorGUILayout.GetControlRect(false, 17);
00458 
00459                 float oldHeight = EditorStyles.popup.fixedHeight;
00460                 EditorStyles.popup.fixedHeight = rect.height;
00461 
00462                 int oldSize = EditorStyles.popup.fontSize;
00463                 EditorStyles.popup.fontSize = 11;
00464 
00465                 m_MaterialPresetSelectionIndex = EditorGUI.Popup(rect, k_MaterialPresetLabel, m_MaterialPresetSelectionIndex, m_MaterialPresetNames);
00466                 if (EditorGUI.EndChangeCheck())
00467                 {
00468                     m_FontSharedMaterialProp.objectReferenceValue = m_MaterialPresets[m_MaterialPresetSelectionIndex];
00469                     m_HavePropertiesChanged = true;
00470                 }
00471 
00472                 //Make sure material preset selection index matches the selection
00473                 if (m_MaterialPresetSelectionIndex < m_MaterialPresetNames.Length && m_TargetMaterial != m_MaterialPresets[m_MaterialPresetSelectionIndex] && !m_HavePropertiesChanged)
00474                     m_IsPresetListDirty = true;
00475 
00476                 EditorStyles.popup.fixedHeight = oldHeight;
00477                 EditorStyles.popup.fontSize = oldSize;
00478             }
00479 
00480             // FONT STYLE
00481             EditorGUI.BeginChangeCheck();
00482 
00483             int v1, v2, v3, v4, v5, v6, v7;
00484 
00485             if (EditorGUIUtility.wideMode)
00486             {
00487                 rect = EditorGUILayout.GetControlRect(true, EditorGUIUtility.singleLineHeight + 2f);
00488 
00489                 EditorGUI.PrefixLabel(rect, k_FontStyleLabel);
00490 
00491                 int styleValue = m_FontStyleProp.intValue;
00492 
00493                 rect.x += EditorGUIUtility.labelWidth;
00494                 rect.width -= EditorGUIUtility.labelWidth;
00495 
00496                 rect.width = Mathf.Max(25f, rect.width / 7f);
00497 
00498                 v1 = TMP_EditorUtility.EditorToggle(rect, (styleValue & 1) == 1, k_BoldLabel, TMP_UIStyleManager.alignmentButtonLeft) ? 1 : 0; // Bold
00499                 rect.x += rect.width;
