00718                 {
00719                     colorMode = m_ColorModeProp;
00720                     topLeft = m_FontColorGradientProp.FindPropertyRelative("topLeft");
00721                     topRight = m_FontColorGradientProp.FindPropertyRelative("topRight");
00722                     bottomLeft = m_FontColorGradientProp.FindPropertyRelative("bottomLeft");
00723                     bottomRight = m_FontColorGradientProp.FindPropertyRelative("bottomRight");
