00728                     colorMode = obj.FindProperty("colorMode");
00729                     topLeft = obj.FindProperty("topLeft");
00730                     topRight = obj.FindProperty("topRight");
00731                     bottomLeft = obj.FindProperty("bottomLeft");
00732                     bottomRight = obj.FindProperty("bottomRight");
00733                 }
00734 
