00747                         TMP_EditorUtility.DrawColorProperty(rect, topLeft);
00748 
00749                         topRight.colorValue = topLeft.colorValue;
00750                         bottomLeft.colorValue = topLeft.colorValue;
00751                         bottomRight.colorValue = topLeft.colorValue;
00752                         break;
00753                     case ColorMode.HorizontalGradient:
00754                         rect.width /= 2f;
00755 
00756                         TMP_EditorUtility.DrawColorProperty(rect, topLeft);
00757 
